background-color: rgba(256,256,256,0.6);
width: 50%;

  margin: 40px; 
box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
@media(min-width: 668px){
  flex-direction: column; 
  width: 70%;   
}
@media(min-width: 1024px){
  width: 80%;
  flex-direction: column; 
}


`
const InfoCard = styled.div`
display: flex;
 align-items: center;



