import React, { useState } from 'react';
import styled from 'styled-components';
import lake from './images/lake.jpg'
const Background = styled.main`
  position: relative;
  width: 100%
  background-image: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.3)), url(${lake});

  height: auto;
  background-size: cover;
  overflow: hidden;
  margin: 10px;

  &after: {
    content: '';
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background: #fff;
  z-index: 0;
  opacity: 0;
  transition: 0.7s;
  }
  &hover: {
    top: 0;
    margin-top: 0;
  }
`
const Section = styled.section`
  position: absolute;
  width: 100%;
  height: 100%;
  top: -60px; /* this sets the visible part's height */
  margin-top: 100%;
  z-index: 1;
  transition: 0.3s;
  background: rgba(255,255,255,0.8);
  &hover: {
    top: 0;
    margin-top: 0;
  }
`



export const About = () => {
  return (
    <Background>
      <Section>TEXT GOES HERE</Section>
    </Background>)
};
