const Section = styled.section`
  position: absolute;
  width: 100%;
  height: 100%;
  top: -60px; /* this sets the visible part's height */
  margin-top: 100%;
  z-index: 1;
  transition: 0.3s;
  background: rgba(255,255,255,0.8);
  &:hover {
    top: 0;
    margin-top: 0;
  }
`

export const About = () => {
  return (
    <Background>
      <ThemeClasses />
